import React, { useEffect, useState } from 'react';
import Tesseract from 'tesseract.js';
export default function ImagetoTextScreen(){
    const [text,setText] = useState("")
    const [loading,setLoading] = useState(true)
    const [progress,setProgress] = useState(0)
    useEffect(()=>{

    },[])

    const handleImageToText = ()=>{
        setProgress(0)
        Tesseract.recognize(
            'https://www.researchgate.net/publication/337702424/figure/fig3/AS:831888872730624@1575349171831/Students-Paragraph-Writing.png',
            'eng',
            { logger: m => setProgress(m?.progress) }
          ).then(({ data: { text } }) => {
            setText(text)
            setLoading(false)
        })
    }
    return(
        <div>
            <h1>This is text </h1>
            {loading ? <p>{`loading text ${Math.round(progress * 100)} %`}</p>: <p>{text}</p> }

        </div>
    )
}
