import React, { useEffect, useState } from 'react';
import Tesseract from 'tesseract.js';

exports.updateImage = async (username = '', image = '') => {
    try {
      const picture = await uploadImage(image, 'dynonary/image');
      const isUpdated = await UserModel.updateOne({ avt: picture });
      if (isUpdated.n && isUpdated.ok) return picture;
      return false;
    } catch (error) {
      throw error;
    }
  };
export default function ImagetoTextScreen(){
    const [text,setText] = useState("")
    const [loading,setLoading] = useState(true)
    const [progress,setProgress] = useState(0)
    const [url,setUrl] = useState("https://www.researchgate.net/publication/337702424/figure/fig3/AS:831888872730624@1575349171831/Students-Paragraph-Writing.png")
    const [country,setCountry] = useState("vie")
    useEffect(()=>{
        handleImageToText()
    },[])

    const handleImageToText = ()=>{
        setProgress(0)
        Tesseract.recognize(
            url,
            country,
            { logger: m => setProgress(m?.progress) }
          ).then(({ data: { text } }) => {
            setText(text)
            setLoading(false)
        })
    }
    return(
        <div>
            <h1>This is text </h1>
            {loading ? <p>{`loading text ${Math.round(progress * 100)} %`}</p>: <p>{text}</p> }

        </div>
    )
}
